javascript:
function removeJSOrCSSFromHTML(fileName, fileType) {
var targetElement = (fileType == "js") ? "script" : (fileType == "css") ? "link" : "none";
var targetAttr = (fileType == "js") ? "src" : (fileType == "css") ? "href" : "none";
var removeList = document.getElementsByTagName(targetElement);
var i;

for (i = removeList.length; i >= 0; i--) {
    if (removeList[i] && removeList[i].getAttribute(targetAttr) != null && removeList[i].getAttribute(targetAttr).indexOf(fileName) != -1) {
        removeList[i].parentNode.removeChild(removeList[i]);
        }
    }
}

removeJSOrCSSFromHTML("pop.js", "js");
removeJSOrCSSFromHTML("pub.js", "js");
